// Toggle Language
let currentLanguage = 'english';

englishBtn.addEventListener("click", () => {
  englishBtn.classList.add("active");
  hindiBtn.classList.remove("active");
  currentLanguage = 'english';
  updateChapterTitle();
  updateVerse();
});

hindiBtn.addEventListener("click", () => {
  hindiBtn.classList.add("active");
  englishBtn.classList.remove("active");
  currentLanguage = 'hindi';
  updateChapterTitle();
  updateVerse();
});

// Function to update active chapter button
function updateActiveChapter(chapterIndex) {
  // Remove the active class from all chapter buttons
  chapterButtons.forEach(button => {
    button.classList.remove('active');
  });

  // Add the active class to the clicked button
  const activeButton = document.querySelector(`.chapter-btn[data-chapter="${chapterIndex}"]`);
  if (activeButton) {
    activeButton.classList.add('active');
  }
}

// Add click event listener to each chapter button
chapterButtons.forEach(button => {
  button.addEventListener('click', (e) => {
    currentChapter = parseInt(e.target.getAttribute('data-chapter'), 10);
    currentVerse = 1; // Reset to the first verse when changing chapters

    // Update the active state of the chapter button
    updateActiveChapter(currentChapter);

    // Update chapter title and verse content
    updateChapterTitle();
    updateVerse();
  });
});

// Function to update chapter title based on selected chapter
function updateChapterTitle() {
  chapterTitle.innerText = chapterNames[currentLanguage][currentChapter];
}

// Function to update verse content
function updateVerse() {
  sanskritText.innerHTML = sanskritVerses[currentChapter][currentVerse - 1];
  translation.innerHTML = currentLanguage === 'english' ? englishTranslations[currentChapter][currentVerse - 1] : hindiTranslations[currentChapter][currentVerse - 1];
  verseImage.src = verseImages[currentChapter][currentVerse - 1];
  updateProgressBar(currentVerse, totalVersesByChapter[currentChapter]);
  updateVerseNumberIndicator();
}

// Function to update the verse number indicator
function updateVerseNumberIndicator() {
    const verseNumberIndicator = document.getElementById("verseNumberIndicator");
    verseNumberIndicator.innerText = `${currentChapter}.${currentVerse}`;
  }

// Update progress bar
function updateProgressBar(verseIndex, totalVerses) {
  const progressPercent = (verseIndex / totalVerses) * 100;
  progress.style.width = `${progressPercent}%`;
}


// Next and Previous buttons
const previousBtn = document.getElementById("previousBtn");
const nextBtn = document.getElementById("nextBtn");

previousBtn.addEventListener("click", () => {
  if (currentVerse > 1) {
    currentVerse--;
    updateVerse();
  } else {
    // If this is the first verse of the current chapter, go to the previous chapter
    if (currentChapter > 1) { // Check if we are not at the first chapter
      currentChapter--; // Move to the previous chapter
      currentVerse = totalVersesByChapter[currentChapter]; // Move to the last verse of the previous chapter
      updateActiveChapter(currentChapter); // Update active chapter button
      updateChapterTitle(); // Update chapter title
      updateVerse(); // Load the last verse of the previous chapter
    }
  }
});

nextBtn.addEventListener("click", () => {
  if (currentVerse < totalVersesByChapter[currentChapter]) {
    currentVerse++;
    updateVerse();
  } else {
    // If this is the last verse of the current chapter, go to the next chapter
    if (currentChapter < 18) { // Check if we are not at the last chapter
      currentChapter++; // Move to the next chapter
      currentVerse = 1; // Start from the first verse of the new chapter
      updateActiveChapter(currentChapter); // Update active chapter button
      updateChapterTitle(); // Update chapter title
      updateVerse(); // Load the first verse of the new chapter
    }
  }
});
// Initialize first chapter as active
updateActiveChapter(currentChapter);
updateChapterTitle();
updateVerse();
updateVerseNumberIndicator();
